// Monitoring
import React, { useState, useEffect } from 'react';
import Header from '../common/Header';
import Sidebar from '../common/Sidebar';
import SearchBar from '../common/SearchBar';
import LoadingSpinner from '../common/LoadingSpinner';
import { useAuth } from '../../context/AuthContext';
import { monitoringService } from '../../services/monitoringService';

const StudentMonitoring = () => {
  const { user } = useAuth();
  const [activities, setActivities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState('all');

  useEffect(() => {
    fetchStudentActivities();
  }, [searchTerm, filterType]);

  const fetchStudentActivities = async () => {
    try {
      setLoading(true);
      const data = await monitoringService.getMyActivities();
      
      // Filter activities for student view
      let filteredActivities = data.activities || [];
      
      // Apply search filter
      if (searchTerm) {
        filteredActivities = filteredActivities.filter(activity =>
          activity.activity_description.toLowerCase().includes(searchTerm.toLowerCase()) ||
          activity.activity_type.toLowerCase().includes(searchTerm.toLowerCase())
        );
      }
      
      // Apply type filter
      if (filterType !== 'all') {
        filteredActivities = filteredActivities.filter(activity =>
          activity.activity_type === filterType
        );
      }
      
      setActivities(filteredActivities);
    } catch (error) {
      console.error('Error fetching student activities:', error);
      setActivities([]);
    } finally {
      setLoading(false);
    }
  };

  const getActivityIcon = (activityType) => {
    switch (activityType) {
      case 'rating_added':
        return 'bi-star-fill text-warning';
      case 'report_viewed':
        return 'bi-eye text-primary';
      case 'login':
        return 'bi-box-arrow-in-right text-success';
      case 'profile_updated':
        return 'bi-person text-info';
      default:
        return 'bi-activity text-secondary';
    }
  };

  const getActivityBadge = (activityType) => {
    switch (activityType) {
      case 'rating_added':
        return 'bg-warning';
      case 'report_viewed':
        return 'bg-primary';
      case 'login':
        return 'bg-success';
      case 'profile_updated':
        return 'bg-info';
      default:
        return 'bg-secondary';
    }
  };

  const formatActivityDescription = (activity) => {
    switch (activity.activity_type) {
      case 'rating_added':
        return `You rated a lecture report`;
      case 'report_viewed':
        return `You viewed a lecture report`;
      case 'login':
        return `You logged into the system`;
      case 'profile_updated':
        return `You updated your profile`;
      default:
        return activity.activity_description;
    }
  };

  if (loading) {
    return (
      <div className="container-fluid">
        <Header />
        <div className="row">
          <Sidebar />
          <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
            <LoadingSpinner message="Loading your activities..." />
          </main>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid">
      <Header />
      <div className="row">
        <Sidebar />
        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">
            <h1 className="h2">My Activities</h1>
            <div className="btn-toolbar mb-2 mb-md-0">
              <span className="badge bg-primary fs-6">
                {activities.length} activities
              </span>
            </div>
          </div>

          {/* Statistics Cards */}
          <div className="row mb-4">
            <div className="col-md-3">
              <div className="card text-white bg-primary">
                <div className="card-body">
                  <div className="d-flex justify-content-between">
                    <div>
                      <h5 className="card-title">Total Activities</h5>
                      <h2 className="mb-0">{activities.length}</h2>
                    </div>
                    <i className="bi bi-activity fs-1 opacity-50"></i>
                  </div>
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="card text-white bg-warning">
                <div className="card-body">
                  <div className="d-flex justify-content-between">
                    <div>
                      <h5 className="card-title">Ratings Given</h5>
                      <h2 className="mb-0">
                        {activities.filter(a => a.activity_type === 'rating_added').length}
                      </h2>
                    </div>
                    <i className="bi bi-star fs-1 opacity-50"></i>
                  </div>
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="card text-white bg-success">
                <div className="card-body">
                  <div className="d-flex justify-content-between">
                    <div>
                      <h5 className="card-title">Reports Viewed</h5>
                      <h2 className="mb-0">
                        {activities.filter(a => a.activity_type === 'report_viewed').length}
                      </h2>
                    </div>
                    <i className="bi bi-eye fs-1 opacity-50"></i>
                  </div>
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="card text-white bg-info">
                <div className="card-body">
                  <div className="d-flex justify-content-between">
                    <div>
                      <h5 className="card-title">This Week</h5>
                      <h2 className="mb-0">
                        {activities.filter(a => {
                          const activityDate = new Date(a.created_at);
                          const oneWeekAgo = new Date();
                          oneWeekAgo.setDate(oneWeekAgo.getDate() - 7);
                          return activityDate > oneWeekAgo;
                        }).length}
                      </h2>
                    </div>
                    <i className="bi bi-calendar-week fs-1 opacity-50"></i>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Search and Filters */}
          <div className="row mb-4">
            <div className="col-md-8">
              <SearchBar 
                onSearch={setSearchTerm}
                placeholder="Search your activities..."
              />
            </div>
            <div className="col-md-4">
              <label className="form-label">Filter by Type</label>
              <select
                className="form-select"
                value={filterType}
                onChange={(e) => setFilterType(e.target.value)}
              >
                <option value="all">All Activities</option>
                <option value="rating_added">Ratings</option>
                <option value="report_viewed">Report Views</option>
                <option value="login">Logins</option>
                <option value="profile_updated">Profile Updates</option>
              </select>
            </div>
          </div>

          {/* Activities Timeline */}
          <div className="card">
            <div className="card-header">
              <h5 className="card-title mb-0">Activity Timeline</h5>
            </div>
            <div className="card-body">
              {activities.length === 0 ? (
                <div className="text-center py-5">
                  <i className="bi bi-activity fs-1 text-muted mb-3 d-block"></i>
                  <h5 className="text-muted">No activities found</h5>
                  <p className="text-muted">
                    {searchTerm || filterType !== 'all' 
                      ? 'Try adjusting your search or filters' 
                      : 'Your activities will appear here as you use the system'
                    }
                  </p>
                </div>
              ) : (
                <div className="timeline">
                  {activities.map((activity, index) => (
                    <div key={activity.id} className="timeline-item mb-4">
                      <div className="row">
                        <div className="col-auto">
                          <div className="timeline-icon">
                            <i className={`bi ${getActivityIcon(activity.activity_type)} fs-4`}></i>
                          </div>
                          {index !== activities.length - 1 && (
                            <div className="timeline-line"></div>
                          )}
                        </div>
                        <div className="col">
                          <div className="card">
                            <div className="card-body">
                              <div className="d-flex justify-content-between align-items-start mb-2">
                                <div>
                                  <span className={`badge ${getActivityBadge(activity.activity_type)} me-2`}>
                                    {activity.activity_type.replace('_', ' ')}
                                  </span>
                                  <small className="text-muted">
                                    {new Date(activity.created_at).toLocaleString()}
                                  </small>
                                </div>
                                <small className="text-muted">
                                  {new Date(activity.created_at).toLocaleDateString() === new Date().toLocaleDateString() 
                                    ? 'Today' 
                                    : new Date(activity.created_at).toLocaleDateString()
                                  }
                                </small>
                              </div>
                              <p className="mb-2">{formatActivityDescription(activity)}</p>
                              {activity.activity_description && (
                                <small className="text-muted">
                                  {activity.activity_description}
                                </small>
                              )}
                              {activity.ip_address && (
                                <div className="mt-2">
                                  <small className="text-muted">
                                    <i className="bi bi-geo-alt me-1"></i>
                                    IP: {activity.ip_address}
                                  </small>
                                </div>
                              )}
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* Activity Summary */}
          {activities.length > 0 && (
            <div className="row mt-4">
              <div className="col-12">
                <div className="card">
                  <div className="card-header">
                    <h5 className="card-title mb-0">Activity Summary</h5>
                  </div>
                  <div className="card-body">
                    <div className="row">
                      <div className="col-md-6">
                        <h6>Activity Distribution</h6>
                        <div className="list-group list-group-flush">
                          {['rating_added', 'report_viewed', 'login', 'profile_updated'].map(type => {
                            const count = activities.filter(a => a.activity_type === type).length;
                            const percentage = activities.length > 0 ? (count / activities.length * 100).toFixed(1) : 0;
                            
                            return (
                              <div key={type} className="list-group-item d-flex justify-content-between align-items-center">
                                <span>
                                  <i className={`bi ${getActivityIcon(type)} me-2`}></i>
                                  {type.replace('_', ' ')}
                                </span>
                                <div>
                                  <span className="badge bg-primary me-2">{count}</span>
                                  <small className="text-muted">{percentage}%</small>
                                </div>
                              </div>
                            );
                          })}
                        </div>
                      </div>
                      <div className="col-md-6">
                        <h6>Recent Activity Trends</h6>
                        <div className="card bg-light">
                          <div className="card-body">
                            <p className="text-muted small">
                              <i className="bi bi-info-circle me-1"></i>
                              This shows your system usage patterns. You can track how actively you're engaging with lecture reports and ratings.
                            </p>
                            <div className="mt-3">
                              <strong>Tips:</strong>
                              <ul className="small text-muted mt-2">
                                <li>Rate lectures promptly after viewing reports</li>
                                <li>Regularly check for new lecture reports</li>
                                <li>Keep your profile information updated</li>
                              </ul>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </main>
      </div>

      <style jsx>{`
        .timeline {
          position: relative;
        }
        .timeline-item {
          position: relative;
        }
        .timeline-icon {
          width: 50px;
          height: 50px;
          border-radius: 50%;
          background: #f8f9fa;
          display: flex;
          align-items: center;
          justify-content: center;
          border: 2px solid #dee2e6;
        }
        .timeline-line {
          width: 2px;
          height: 100%;
          background: #dee2e6;
          position: absolute;
          left: 24px;
          top: 50px;
        }
      `}</style>
    </div>
  );
};

export default StudentMonitoring;
