// pages/Ratings.jsx
import React, { useState, useEffect } from 'react';
import Header from '../components/common/Header';
import Sidebar from '../components/common/Sidebar';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import { Modal, Button, Form } from 'react-bootstrap';

const Ratings = () => {
  const { user } = useAuth();
  const [ratings, setRatings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Modal state
  const [showModal, setShowModal] = useState(false);
  const [selectedReport, setSelectedReport] = useState(null);
  const [ratingValue, setRatingValue] = useState(5);
  const [categoryValue, setCategoryValue] = useState('lecture');
  const [commentValue, setCommentValue] = useState('');

  useEffect(() => {
    fetchRatingsData();
  }, []);

  const fetchRatingsData = async () => {
    try {
      setLoading(true);
      const res = await api.get('/ratings');

      if (user.role === 'student') {
        setRatings(res.data.ratings || []);
      } else {
        setRatings(res.data.ratings || res.data.summary || []);
      }

    } catch (err) {
      console.error('Error fetching ratings:', err);
      setError('Failed to load ratings data');
    } finally {
      setLoading(false);
    }
  };

  const openRatingModal = (report) => {
    setSelectedReport(report);
    setRatingValue(report.rating || 5);
    setCategoryValue(report.rating_type || 'lecture');
    setCommentValue(report.comment || '');
    setShowModal(true);
  };

  const handleSubmitRating = async () => {
    if (!selectedReport) return;

    try {
      await api.post('/ratings', {
        report_id: selectedReport.report_id,
        rating: ratingValue,
        rating_type: categoryValue,
        comment: commentValue,
      });
      setShowModal(false);
      fetchRatingsData();
      alert('Rating submitted successfully!');
    } catch (err) {
      console.error('Error submitting rating:', err);
      alert('Failed to submit rating');
    }
  };

  const renderStarRating = (rating) => (
    <div className="d-flex align-items-center">
      <span className="text-warning me-2">
        {'★'.repeat(Math.floor(rating))}{'☆'.repeat(5 - Math.floor(rating))}
      </span>
      <span className="badge bg-primary">{rating}/5</span>
    </div>
  );

  if (loading) return (
    <div className="spinner-border text-primary m-5" role="status">
      <span className="visually-hidden">Loading...</span>
    </div>
  );

  return (
    <div className="container-fluid">
      <Header />
      <div className="row">
        <Sidebar />
        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <div className="d-flex justify-content-between pt-3 pb-2 mb-3 border-bottom">
            <h1 className="h2">
              {user.role === 'student' ? 'My Ratings' : 'Lecture Ratings Summary'}
            </h1>
            <Button size="sm" variant="outline-secondary" onClick={fetchRatingsData}>Refresh</Button>
          </div>

          {error && <div className="alert alert-danger">{error}</div>}

          {ratings.length === 0 && (
            <div className="alert alert-info">
              {user.role === 'student' 
                ? 'No ratings yet. Go to Reports to rate lectures.' 
                : 'No ratings available for this course yet.'}
            </div>
          )}

          {user.role === 'student' ? (
            <div className="table-responsive">
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th>Class</th>
                    <th>Course</th>
                    <th>Rating</th>
                    <th>Category</th>
                    <th>Comment</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {ratings.map(r => (
                    <tr key={r.report_id}>
                      <td>{r.class_name}</td>
                      <td>{r.course_name}</td>
                      <td>{r.rating ? renderStarRating(r.rating) : 'Not Rated'}</td>
                      <td>{r.rating_type || '-'}</td>
                      <td>{r.comment || '-'}</td>
                      <td>
                        <Button size="sm" onClick={() => openRatingModal(r)}>
                          {r.rating ? 'Edit Rating' : 'Rate'}
                        </Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th>Class</th>
                    <th>Course</th>
                    <th>Average Rating</th>
                    <th># of Ratings</th>
                  </tr>
                </thead>
                <tbody>
                  {ratings.map(r => (
                    <tr key={r.report_id}>
                      <td>{r.class_name}</td>
                      <td>{r.course_name}</td>
                      <td>{r.average_rating ? renderStarRating(r.average_rating) : '-'}</td>
                      <td>{r.num_ratings || 0}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {/* Rating Modal */}
          <Modal show={showModal} onHide={() => setShowModal(false)}>
            <Modal.Header closeButton>
              <Modal.Title>Rate Lecture: {selectedReport?.course_name}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <Form>
                <Form.Group className="mb-3">
                  <Form.Label>Category</Form.Label>
                  <Form.Select value={categoryValue} onChange={e => setCategoryValue(e.target.value)}>
                    <option value="lecture">Lecture</option>
                    <option value="content">Content</option>
                    <option value="delivery">Delivery</option>
                    <option value="general">General</option>
                  </Form.Select>
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Rating (1–5)</Form.Label>
                  <Form.Control 
                    type="number" min={1} max={5} 
                    value={ratingValue} 
                    onChange={e => setRatingValue(parseInt(e.target.value))} 
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Comment</Form.Label>
                  <Form.Control 
                    as="textarea" rows={3} 
                    value={commentValue} 
                    onChange={e => setCommentValue(e.target.value)} 
                  />
                </Form.Group>
              </Form>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={() => setShowModal(false)}>Cancel</Button>
              <Button variant="primary" onClick={handleSubmitRating}>Submit</Button>
            </Modal.Footer>
          </Modal>
        </main>
      </div>
    </div>
  );
};

export default Ratings;
