// pages/CourseManagement.jsx
import React, { useState, useEffect } from 'react';
import Header from '../common/Header';
import Sidebar from '../common/Sidebar';
import { useAuth } from '../../context/AuthContext';
import { Modal, Button, Form } from 'react-bootstrap';

const CourseManagement = () => {
  const { user } = useAuth();
  const [courses, setCourses] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [showRatingModal, setShowRatingModal] = useState(false);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [ratingValue, setRatingValue] = useState(5);
  const [categoryValue, setCategoryValue] = useState('lecture');
  const [commentValue, setCommentValue] = useState('');
  const [formData, setFormData] = useState({
    course_code: '',
    course_name: '',
    credits: 4,
    stream: 'IT',
    program_leader_id: user?.id || null,
  });

  useEffect(() => {
    fetchCourses();
    fetchRatings();
  }, []);

  // ----- Courses -----
  const fetchCourses = () => {
    setLoading(true);
    const storedCourses = JSON.parse(localStorage.getItem('courses')) || [];
    setCourses(storedCourses);
    setLoading(false);
  };

  const saveCourses = (newCourses) => {
    localStorage.setItem('courses', JSON.stringify(newCourses));
    setCourses(newCourses);
  };

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmitCourse = (e) => {
    e.preventDefault();
    const newCourse = { ...formData, id: Date.now(), created_at: new Date().toISOString() };
    const updatedCourses = [...courses, newCourse];
    saveCourses(updatedCourses);
    setFormData({
      course_code: '',
      course_name: '',
      credits: 4,
      stream: 'IT',
      program_leader_id: user?.id || null,
    });
    setShowForm(false);
  };

  const handleDeleteCourse = (id) => {
    if (!window.confirm('Delete this course?')) return;
    const updatedCourses = courses.filter(c => c.id !== id);
    saveCourses(updatedCourses);
    const updatedRatings = ratings.filter(r => r.course_id !== id);
    saveRatings(updatedRatings);
  };

  // ----- Ratings -----
  const fetchRatings = () => {
    const storedRatings = JSON.parse(localStorage.getItem('ratings')) || [];
    setRatings(storedRatings);
  };

  const saveRatings = (newRatings) => {
    localStorage.setItem('ratings', JSON.stringify(newRatings));
    setRatings(newRatings);
  };

  const openRatingModal = (course) => {
    setSelectedCourse(course);
    const existingRating = ratings.find(r => r.course_id === course.id && r.student_id === user.id);
    setRatingValue(existingRating?.rating || 5);
    setCategoryValue(existingRating?.rating_type || 'lecture');
    setCommentValue(existingRating?.comment || '');
    setShowRatingModal(true);
  };

  const handleSubmitRating = () => {
    if (!selectedCourse) return;
    const existingIndex = ratings.findIndex(r => r.course_id === selectedCourse.id && r.student_id === user.id);
    const newRating = {
      id: existingIndex >= 0 ? ratings[existingIndex].id : Date.now(),
      course_id: selectedCourse.id,
      student_id: user.id,
      rating: ratingValue,
      rating_type: categoryValue,
      comment: commentValue,
      created_at: new Date().toISOString(),
    };

    let updatedRatings;
    if (existingIndex >= 0) {
      updatedRatings = [...ratings];
      updatedRatings[existingIndex] = newRating;
    } else {
      updatedRatings = [...ratings, newRating];
    }

    saveRatings(updatedRatings);
    setShowRatingModal(false);
  };

  const renderStarRating = (rating) => (
    <span>{'★'.repeat(Math.floor(rating))}{'☆'.repeat(5 - Math.floor(rating))} ({rating}/5)</span>
  );

  // ----- Ratings Summary for lecturers -----
  const renderRatingsSummary = () => {
    if (!courses.length) return null;
    return (
      <div className="row mb-4">
        {courses.map(course => {
          const courseRatings = ratings.filter(r => r.course_id === course.id);
          const totalRatings = courseRatings.length;
          const avgRating = totalRatings ? (courseRatings.reduce((a,b)=>a+b.rating,0)/totalRatings).toFixed(2) : 0;
          const studentsRated = totalRatings; // since each rating is per student
          return (
            <div key={course.id} className="col-md-4 mb-3">
              <div className="card text-center">
                <div className="card-header"><strong>{course.course_name}</strong></div>
                <div className="card-body">
                  <p>Total Ratings: <strong>{totalRatings}</strong></p>
                  <p>Average Rating: <strong>{avgRating}</strong></p>
                  <p>Students Rated: <strong>{studentsRated}</strong></p>
                </div>
              </div>
            </div>
          )
        })}
      </div>
    );
  }

  if (loading) return <div>Loading...</div>;

  return (
    <div className="container-fluid">
      <Header />
      <div className="row">
        <Sidebar />
        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <div className="d-flex justify-content-between align-items-center pt-3 pb-2 mb-3 border-bottom">
            <h1 className="h2">Course Management</h1>
            {user?.role === 'program_leader' && (
              <button className="btn btn-primary" onClick={() => setShowForm(true)}>Add Course</button>
            )}
          </div>

          {/* Ratings Summary only for lecturer/program leader */}
          {(user?.role === 'program_leader') && renderRatingsSummary()}

          {/* Course Form */}
          {showForm && (
            <div className="card mb-4">
              <div className="card-header"><h5>Add New Course</h5></div>
              <div className="card-body">
                <form onSubmit={handleSubmitCourse}>
                  <div className="row mb-3">
                    <div className="col-md-6">
                      <label>Course Code *</label>
                      <input type="text" name="course_code" value={formData.course_code} onChange={handleChange} className="form-control" required />
                    </div>
                    <div className="col-md-6">
                      <label>Course Name *</label>
                      <input type="text" name="course_name" value={formData.course_name} onChange={handleChange} className="form-control" required />
                    </div>
                  </div>
                  <div className="row mb-3">
                    <div className="col-md-6">
                      <label>Credits</label>
                      <select name="credits" value={formData.credits} onChange={handleChange} className="form-select">
                        {[2,3,4,5,6].map(c => <option key={c} value={c}>{c}</option>)}
                      </select>
                    </div>
                    <div className="col-md-6">
                      <label>Stream</label>
                      <select name="stream" value={formData.stream} onChange={handleChange} className="form-select">
                        {['IT','Business','Business IT','Fashion','Design','Tourism'].map(s => <option key={s} value={s}>{s}</option>)}
                      </select>
                    </div>
                  </div>
                  <button type="submit" className="btn btn-success me-2">Create</button>
                  <button type="button" className="btn btn-secondary" onClick={() => setShowForm(false)}>Cancel</button>
                </form>
              </div>
            </div>
          )}

          {/* Courses Table */}
          <div className="card mb-4">
            <div className="card-header"><h5>Available Courses</h5></div>
            <div className="card-body table-responsive">
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Code</th>
                    <th>Name</th>
                    <th>Credits</th>
                    <th>Stream</th>
                    <th>Average Rating</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {courses.map(course => {
                    const courseRatings = ratings.filter(r => r.course_id === course.id);
                    const avgRating = courseRatings.length
                      ? (courseRatings.reduce((a,b)=>a+b.rating,0)/courseRatings.length).toFixed(2)
                      : 0;
                    return (
                      <tr key={course.id}>
                        <td>{course.id}</td>
                        <td>{course.course_code}</td>
                        <td>{course.course_name}</td>
                        <td>{course.credits}</td>
                        <td>{course.stream}</td>
                        <td>{renderStarRating(avgRating)}</td>
                        <td>
                          {user?.role === 'student' && (
                            <button className="btn btn-sm btn-primary me-2" onClick={()=>openRatingModal(course)}>Rate</button>
                          )}
                          {user?.role === 'program_leader' && (
                            <button className="btn btn-sm btn-danger" onClick={()=>handleDeleteCourse(course.id)}>Delete</button>
                          )}
                        </td>
                      </tr>
                    )
                  })}
                </tbody>
              </table>
            </div>
          </div>

          {/* Rating Modal */}
          <Modal show={showRatingModal} onHide={()=>setShowRatingModal(false)}>
            <Modal.Header closeButton>
              <Modal.Title>Rate Course: {selectedCourse?.course_name}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <Form>
                <Form.Group className="mb-3">
                  <Form.Label>Category</Form.Label>
                  <Form.Select value={categoryValue} onChange={e=>setCategoryValue(e.target.value)}>
                    <option value="lecture">Lecture</option>
                    <option value="content">Content</option>
                    <option value="delivery">Delivery</option>
                    <option value="general">General</option>
                  </Form.Select>
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Rating (1-5)</Form.Label>
                  <Form.Control type="number" min={1} max={5} value={ratingValue} onChange={e=>setRatingValue(parseInt(e.target.value))}/>
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Comment</Form.Label>
                  <Form.Control as="textarea" rows={3} value={commentValue} onChange={e=>setCommentValue(e.target.value)}/>
                </Form.Group>
              </Form>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={()=>setShowRatingModal(false)}>Cancel</Button>
              <Button variant="primary" onClick={handleSubmitRating}>Submit Rating</Button>
            </Modal.Footer>
          </Modal>
        </main>
      </div>
    </div>
  );
};

export default CourseManagement;
