import React, { useState, useEffect } from 'react';
import Header from '../components/common/Header';
import Sidebar from '../components/common/Sidebar';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import { Modal, Button, Form } from 'react-bootstrap';

const Ratings = () => {
  const { user } = useAuth();
  const [ratings, setRatings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Modal state
  const [showModal, setShowModal] = useState(false);
  const [selectedReport, setSelectedReport] = useState(null);
  const [ratingValue, setRatingValue] = useState(5);
  const [categoryValue, setCategoryValue] = useState('lecture');
  const [commentValue, setCommentValue] = useState('');

  useEffect(() => {
    fetchRatingsData();
  }, []);

  const fetchRatingsData = async () => {
    try {
      setLoading(true);
      let endpoint = '/ratings';

      if (user?.role === 'lecturer' || user?.role === 'program_leader') {
        endpoint = '/ratings/lecturer/summary';
      }

      const response = await api.get(endpoint);

      if (user?.role === 'student') setRatings(response.data.ratings || []);
      else setRatings(response.data.summary || []);
    } catch (err) {
      console.error(err);
      setError('Failed to load ratings data');
    } finally {
      setLoading(false);
    }
  };

  const openRatingModal = (report) => {
    setSelectedReport(report);
    setRatingValue(report.rating || 5);
    setCategoryValue(report.rating_type || 'lecture');
    setCommentValue(report.comment || '');
    setShowModal(true);
  };

  const handleSubmitRating = async () => {
    try {
      await api.post('/ratings', {
        report_id: selectedReport.report_id || selectedReport.id,
        rating: ratingValue,
        rating_type: categoryValue,
        comment: commentValue,
      });
      setShowModal(false);
      fetchRatingsData();
      alert('Rating submitted successfully!');
    } catch (err) {
      console.error(err);
      alert('Failed to submit rating');
    }
  };

  const renderStarRating = (rating) => (
    <div className="d-flex align-items-center">
      <span className="text-warning me-2">
        {'★'.repeat(Math.floor(rating))}{'☆'.repeat(5 - Math.floor(rating))}
      </span>
      <span className="badge bg-primary">{rating}/5</span>
    </div>
  );

  if (loading) return <div className="container-fluid">Loading...</div>;

  const renderStudentView = () => (
    <div className="row">
      <div className="col-12">
        {ratings.length === 0 ? (
          <p>No ratings yet.</p>
        ) : (
          <table className="table table-hover">
            <thead>
              <tr>
                <th>Class</th>
                <th>Rating</th>
                <th>Category</th>
                <th>Comment</th>
                <th>Date</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {ratings.map(r => (
                <tr key={r.id}>
                  <td>{r.class_name} <br/><small>{r.course_name}</small></td>
                  <td>{renderStarRating(r.rating)}</td>
                  <td>{r.rating_type}</td>
                  <td>{r.comment || 'No comment'}</td>
                  <td>{new Date(r.created_at).toLocaleDateString()}</td>
                  <td><button onClick={() => openRatingModal(r)} className="btn btn-sm btn-outline-primary">Rate/Edit</button></td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );

  const renderLecturerView = () => (
    <div className="row">
      <div className="col-12">
        <table className="table table-hover">
          <thead>
            <tr>
              <th>Class</th>
              <th>Average Rating</th>
              <th>Number of Ratings</th>
            </tr>
          </thead>
          <tbody>
            {ratings.map(r => (
              <tr key={r.report_id}>
                <td>{r.class_name} <br/><small>{r.course_name}</small></td>
                <td>{renderStarRating(r.average_rating)}</td>
                <td>{r.num_ratings}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );

  return (
    <div className="container-fluid">
      <Header />
      <div className="row">
        <Sidebar />
        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          {error && <div className="alert alert-danger">{error}</div>}
          {user?.role === 'student' ? renderStudentView() : renderLecturerView()}

          <Modal show={showModal} onHide={() => setShowModal(false)}>
            <Modal.Header closeButton>
              <Modal.Title>Rate Lecture: {selectedReport?.course_name}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <Form>
                <Form.Group>
                  <Form.Label>Category</Form.Label>
                  <Form.Select value={categoryValue} onChange={e => setCategoryValue(e.target.value)}>
                    <option value="lecture">Lecture</option>
                    <option value="content">Content</option>
                    <option value="delivery">Delivery</option>
                    <option value="general">General</option>
                  </Form.Select>
                </Form.Group>
                <Form.Group>
                  <Form.Label>Rating (1-5)</Form.Label>
                  <Form.Control type="number" min={1} max={5} value={ratingValue} onChange={e => setRatingValue(parseInt(e.target.value))} />
                </Form.Group>
                <Form.Group>
                  <Form.Label>Comment</Form.Label>
                  <Form.Control as="textarea" rows={3} value={commentValue} onChange={e => setCommentValue(e.target.value)} />
                </Form.Group>
              </Form>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={() => setShowModal(false)}>Cancel</Button>
              <Button variant="primary" onClick={handleSubmitRating}>Submit Rating</Button>
            </Modal.Footer>
          </Modal>
        </main>
      </div>
    </div>
  );
};

export default Ratings;
