import React, { useState, useEffect } from 'react';
import Header from '../components/common/Header';
import Sidebar from '../components/common/Sidebar';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import { Modal, Button, Form } from 'react-bootstrap';

const Ratings = () => {
  const { user } = useAuth();
  const [ratings, setRatings] = useState([]);
  const [statistics, setStatistics] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Modal state
  const [showModal, setShowModal] = useState(false);
  const [selectedReport, setSelectedReport] = useState(null);
  const [ratingValue, setRatingValue] = useState(5);
  const [categoryValue, setCategoryValue] = useState('lecture');
  const [commentValue, setCommentValue] = useState('');

  useEffect(() => {
    fetchRatingsData();
  }, []);

  const fetchRatingsData = async () => {
    try {
      setLoading(true);

      let ratingsEndpoint = '/ratings';
      if (user?.role === 'lecturer') {
        ratingsEndpoint = '/ratings/lecturer/summary';
      } else if (user?.role === 'student') {
        ratingsEndpoint = '/ratings?limit=50';
      }

      const ratingsResponse = await api.get(ratingsEndpoint);

      if (user?.role === 'lecturer' || user?.role === 'program_leader') {
        setRatings(ratingsResponse.data.summary || []);
        const statsResponse = await api.get('/ratings/stats/summary');
        setStatistics(statsResponse.data.statistics);
      } else {
        setRatings(ratingsResponse.data.ratings || []);
      }
    } catch (err) {
      setError('Failed to load ratings data');
      console.error('Error fetching ratings:', err);
    } finally {
      setLoading(false);
    }
  };

  const openRatingModal = (report) => {
    setSelectedReport(report);
    setRatingValue(report.rating || 5);
    setCategoryValue(report.category || 'lecture');
    setCommentValue(report.comment || '');
    setShowModal(true);
  };

  const handleSubmitRating = async () => {
    try {
      await api.post('/ratings', {
        report_id: selectedReport.id,
        rating: ratingValue,
        comment: commentValue,
        category: categoryValue
      });
      setShowModal(false);
      fetchRatingsData();
      alert('Rating submitted successfully!');
    } catch (err) {
      console.error('Error submitting rating:', err);
      alert('Failed to submit rating');
    }
  };

  const renderStarRating = (rating) => (
    <div className="d-flex align-items-center">
      <span className="text-warning me-2">
        {'★'.repeat(Math.floor(rating))}{'☆'.repeat(5 - Math.floor(rating))}
      </span>
      <span className="badge bg-primary">{rating}/5</span>
    </div>
  );

  if (loading) {
    return (
      <div className="container-fluid">
        <Header />
        <div className="row">
          <Sidebar />
          <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
            <div className="d-flex justify-content-center align-items-center" style={{ height: '50vh' }}>
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Loading...</span>
              </div>
            </div>
          </main>
        </div>
      </div>
    );
  }

  // Student view
  const renderStudentView = () => (
    <div className="row">
      <div className="col-12">
        {ratings.length === 0 ? (
          <div className="text-center py-4">
            <i className="fas fa-star fa-3x text-muted mb-3"></i>
            <h5>No ratings submitted yet</h5>
            <p className="text-muted">You haven't rated any lectures yet.</p>
            <button className="btn btn-primary" onClick={() => openRatingModal({ id: 0, course_name: 'Sample Lecture' })}>
              Go to Reports to Rate Lectures
            </button>
          </div>
        ) : (
          <div className="table-responsive">
            <table className="table table-hover">
              <thead>
                <tr>
                  <th>Class</th>
                  <th>Rating</th>
                  <th>Category</th>
                  <th>Comment</th>
                  <th>Date</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {ratings.map((rating) => (
                  <tr key={rating.id}>
                    <td>
                      <strong>{rating.class_name}</strong>
                      <br />
                      <small className="text-muted">{rating.course_name}</small>
                    </td>
                    <td>{renderStarRating(rating.rating)}</td>
                    <td>
                      <span className={`badge ${
                        rating.category === 'lecture' ? 'bg-primary' :
                        rating.category === 'content' ? 'bg-info' :
                        rating.category === 'delivery' ? 'bg-success' : 'bg-secondary'
                      }`}>
                        {rating.category}
                      </span>
                    </td>
                    <td>{rating.comment || 'No comment'}</td>
                    <td>{new Date(rating.created_at).toLocaleDateString()}</td>
                    <td>
                      <button
                        className="btn btn-sm btn-outline-primary"
                        onClick={() => openRatingModal(rating)}
                      >
                        Rate / Edit
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );

  // Lecturer view
  const renderLecturerView = () => (
    <div className="row">
      {statistics && (
        <div className="col-12 mb-3">
          <div className="card">
            <div className="card-header">
              <h5 className="card-title mb-0">Teaching Statistics</h5>
            </div>
            <div className="card-body">
              <div className="row">
                <div className="col-md-4">
                  <div className="alert alert-primary text-center">
                    <h6>Total Ratings</h6>
                    <h3>{statistics.total_ratings}</h3>
                  </div>
                </div>
                <div className="col-md-4">
                  <div className="alert alert-success text-center">
                    <h6>Average Rating</h6>
                    <h3>{statistics.average_rating?.toFixed(2) || 0}</h3>
                  </div>
                </div>
                <div className="col-md-4">
                  <div className="alert alert-info text-center">
                    <h6>Lectures Rated</h6>
                    <h3>{statistics.lectures_rated}</h3>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      <div className="col-12">
        <div className="card">
          <div className="card-header">
            <h5 className="card-title mb-0">Lecture Ratings Summary</h5>
          </div>
          <div className="card-body table-responsive">
            <table className="table table-hover">
              <thead>
                <tr>
                  <th>Class</th>
                  <th>Average Rating</th>
                  <th>Number of Ratings</th>
                </tr>
              </thead>
              <tbody>
                {ratings.map((r) => (
                  <tr key={r.class_id || r.id}>
                    <td>
                      <strong>{r.class_name}</strong>
                      <br />
                      <small className="text-muted">{r.course_name}</small>
                    </td>
                    <td>{renderStarRating(r.average_rating)}</td>
                    <td>{r.num_ratings}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="container-fluid">
      <Header />
      <div className="row">
        <Sidebar />
        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">
            <h1 className="h2">{user?.role === 'student' ? 'My Ratings' : 'Teaching Ratings'}</h1>
            <div className="btn-toolbar mb-2 mb-md-0">
              <div className="btn-group me-2">
                <button type="button" className="btn btn-sm btn-outline-secondary" onClick={fetchRatingsData}>
                  <i className="fas fa-sync-alt me-1"></i> Refresh
                </button>
              </div>
            </div>
          </div>

          {error && (
            <div className="alert alert-danger alert-dismissible fade show" role="alert">
              {error}
              <button type="button" className="btn-close" onClick={() => setError('')}></button>
            </div>
          )}

          {user?.role === 'student' ? renderStudentView() : renderLecturerView()}

          {/* Rating Modal */}
          <Modal show={showModal} onHide={() => setShowModal(false)}>
            <Modal.Header closeButton>
              <Modal.Title>Rate Lecture: {selectedReport?.course_name}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <Form>
                <Form.Group className="mb-3">
                  <Form.Label>Category</Form.Label>
                  <Form.Select value={categoryValue} onChange={(e) => setCategoryValue(e.target.value)}>
                    <option value="lecture">Lecture</option>
                    <option value="content">Content</option>
                    <option value="delivery">Delivery</option>
                    <option value="general">General</option>
                  </Form.Select>
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Rating (1–5)</Form.Label>
                  <Form.Control
                    type="number"
                    min={1}
                    max={5}
                    value={ratingValue}
                    onChange={(e) => setRatingValue(parseInt(e.target.value))}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Comment</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={3}
                    value={commentValue}
                    onChange={(e) => setCommentValue(e.target.value)}
                  />
                </Form.Group>
              </Form>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={() => setShowModal(false)}>Cancel</Button>
              <Button variant="primary" onClick={handleSubmitRating}>Submit Rating</Button>
            </Modal.Footer>
          </Modal>

        </main>
      </div>
    </div>
  );
};

export default Ratings;
