// src/App.jsx
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import Dashboard from './pages/Dashboard';
import Reports from './pages/Reports';
import Courses from './pages/Courses';
import Monitoring from './pages/Monitoring';
import Ratings from './pages/Ratings';
import HomePage from './pages/HomePage';

import StudentDashboard from './components/student/StudentDashboard';
import StudentMonitoring from './components/student/Monitoring';
import ReportForm from './components/lecturer/ReportForm';
import CourseManagement from './components/program-leader/CourseManagement';
import ClassManagement from './components/program-leader/ClassManagement';
import UserManagement from './components/program-leader/UserManagement';
import ProtectedRoute from './utils/ProtectedRoute';
import RoleBasedRoute from './utils/RoleBasedRoute';
import './styles/App.css';

import Profile from './components/common/Profile';
import Settings from './components/common/Settings';
import NotFound from './components/common/NotFound';
import Navbar from './components/common/Navbar'; // <-- top navbar
import Footer from './components/common/Footer';

function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="App d-flex flex-column min-vh-100">
          {/* Top Navbar */}
          <Navbar />

          {/* Main Content */}
          <div className="flex-grow-1 container mt-4">
            <Routes>
              {/* Public Routes */}
              <Route path="/" element={<HomePage />} />
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />

              {/* Protected Routes */}
              <Route
                path="/dashboard"
                element={
                  <ProtectedRoute>
                    <Dashboard />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/student/dashboard"
                element={
                  <RoleBasedRoute allowedRoles={['student']}>
                    <StudentDashboard />
                  </RoleBasedRoute>
                }
              />

              {/* Reports Routes */}
              <Route
                path="/reports"
                element={
                  <ProtectedRoute>
                    <Reports />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/reports/new"
                element={
                  <RoleBasedRoute allowedRoles={['lecturer']}>
                    <ReportForm />
                  </RoleBasedRoute>
                }
              />
              <Route
                path="/reports/edit/:id"
                element={
                  <RoleBasedRoute allowedRoles={['lecturer']}>
                    <ReportForm />
                  </RoleBasedRoute>
                }
              />

              {/* Courses Routes */}
              <Route
                path="/courses"
                element={
                  <ProtectedRoute>
                    <Courses />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/courses/management"
                element={
                  <RoleBasedRoute allowedRoles={['program_leader']}>
                    <CourseManagement />
                  </RoleBasedRoute>
                }
              />

              {/* Classes Routes */}
              <Route
                path="/classes/management"
                element={
                  <RoleBasedRoute allowedRoles={['program_leader']}>
                    <ClassManagement />
                  </RoleBasedRoute>
                }
              />

              {/* Monitoring Routes */}
              <Route
                path="/monitoring"
                element={
                  <ProtectedRoute>
                    <Monitoring />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/student/monitoring"
                element={
                  <RoleBasedRoute allowedRoles={['student']}>
                    <StudentMonitoring />
                  </RoleBasedRoute>
                }
              />

              {/* Ratings Routes */}
              <Route
                path="/ratings"
                element={
                  <ProtectedRoute>
                    <Ratings />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/ratings/class/:classId"
                element={
                  <ProtectedRoute>
                    <Ratings />
                  </ProtectedRoute>
                }
              />

              {/* User Management Routes */}
              <Route
                path="/users/management"
                element={
                  <RoleBasedRoute allowedRoles={['program_leader', 'principal_lecturer']}>
                    <UserManagement />
                  </RoleBasedRoute>
                }
              />

              {/* Profile & Settings */}
              <Route
                path="/profile"
                element={
                  <ProtectedRoute>
                    <Profile />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/settings"
                element={
                  <ProtectedRoute>
                    <Settings />
                  </ProtectedRoute>
                }
              />

              {/* Catch all */}
              <Route path="*" element={<NotFound />} />
            </Routes>
          </div>

          {/* Footer */}
          <Footer />
        </div>
      </Router>
    </AuthProvider>
  );
}

// Optional redirect helper
const NavigateToDashboard = () => {
  const token = localStorage.getItem('token');
  if (!token) return <Navigate to="/login" />;
  return <Navigate to="/dashboard" />;
};

export default App;
