import React, { useState, useEffect } from 'react';
import Header from '../components/common/Header';
import Sidebar from '../components/common/Sidebar';
import { useAuth } from '../context/AuthContext';
import { Modal, Button, Form } from 'react-bootstrap';

const Ratings = () => {
  const { user } = useAuth();
  const [ratings, setRatings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Modal state
  const [showModal, setShowModal] = useState(false);
  const [selectedReport, setSelectedReport] = useState(null);
  const [ratingValue, setRatingValue] = useState(5);
  const [categoryValue, setCategoryValue] = useState('lecture');
  const [commentValue, setCommentValue] = useState('');

  useEffect(() => {
    fetchRatingsData();
  }, []);

  const fetchRatingsData = () => {
    try {
      setLoading(true);
      const storedRatings = JSON.parse(localStorage.getItem('ratings')) || [];
      setRatings(storedRatings);
    } catch (err) {
      console.error('Error loading ratings from localStorage:', err);
      setError('Failed to load ratings data');
    } finally {
      setLoading(false);
    }
  };

  const openRatingModal = (report) => {
    const existingRating = ratings.find(r => r.report_id === report.id && r.user_id === user.id);
    setSelectedReport(report);
    setRatingValue(existingRating?.rating || 5);
    setCategoryValue(existingRating?.rating_type || 'lecture');
    setCommentValue(existingRating?.comment || '');
    setShowModal(true);
  };

  const handleSubmitRating = () => {
    try {
      const storedRatings = JSON.parse(localStorage.getItem('ratings')) || [];

      const existingIndex = storedRatings.findIndex(
        r => r.report_id === selectedReport.id && r.user_id === user.id
      );

      const newRating = {
        report_id: selectedReport.id,
        class_name: selectedReport.class_name || 'Sample Class',
        course_name: selectedReport.course_name || 'Sample Lecture',
        rating: ratingValue,
        rating_type: categoryValue,
        comment: commentValue,
        created_at: new Date().toISOString(),
        user_id: user.id,
      };

      if (existingIndex >= 0) {
        // Update existing rating
        storedRatings[existingIndex] = newRating;
      } else {
        // Add new rating
        storedRatings.push(newRating);
      }

      localStorage.setItem('ratings', JSON.stringify(storedRatings));
      setShowModal(false);
      fetchRatingsData();
      alert('Rating submitted successfully!');
    } catch (err) {
      console.error('Error saving rating:', err);
      alert('Failed to submit rating');
    }
  };

  const renderStarRating = (rating) => (
    <div className="d-flex align-items-center">
      <span className="text-warning me-2">
        {'★'.repeat(Math.floor(rating))}{'☆'.repeat(5 - Math.floor(rating))}
      </span>
      <span className="badge bg-primary">{rating}/5</span>
    </div>
  );

  if (loading) {
    return (
      <div className="container-fluid">
        <Header />
        <div className="row">
          <Sidebar />
          <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
            <div className="d-flex justify-content-center align-items-center" style={{ height: '50vh' }}>
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Loading...</span>
              </div>
            </div>
          </main>
        </div>
      </div>
    );
  }

  const renderStudentView = () => (
    <div className="row">
      <div className="col-12">
        {ratings.filter(r => r.user_id === user.id).length === 0 ? (
          <div className="text-center py-4">
            <i className="fas fa-star fa-3x text-muted mb-3"></i>
            <h5>No ratings submitted yet</h5>
            <p className="text-muted">You haven't rated any lectures yet.</p>
            <button
              className="btn btn-primary"
              onClick={() => openRatingModal({ id: Date.now(), course_name: 'Sample Lecture' })}
            >
              Go to Reports to Rate Lectures
            </button>
          </div>
        ) : (
          <div className="table-responsive">
            <table className="table table-hover">
              <thead>
                <tr>
                  <th>Class</th>
                  <th>Rating</th>
                  <th>Category</th>
                  <th>Comment</th>
                  <th>Date</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {ratings
                  .filter(r => r.user_id === user.id)
                  .map((rating) => (
                    <tr key={rating.report_id}>
                      <td>
                        <strong>{rating.class_name}</strong>
                        <br />
                        <small className="text-muted">{rating.course_name}</small>
                      </td>
                      <td>{renderStarRating(rating.rating)}</td>
                      <td>
                        <span
                          className={`badge ${
                            rating.rating_type === 'lecture'
                              ? 'bg-primary'
                              : rating.rating_type === 'content'
                              ? 'bg-info'
                              : rating.rating_type === 'delivery'
                              ? 'bg-success'
                              : 'bg-secondary'
                          }`}
                        >
                          {rating.rating_type}
                        </span>
                      </td>
                      <td>{rating.comment || 'No comment'}</td>
                      <td>{new Date(rating.created_at).toLocaleDateString()}</td>
                      <td>
                        <button
                          className="btn btn-sm btn-outline-primary"
                          onClick={() => openRatingModal(rating)}
                        >
                          Rate / Edit
                        </button>
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );

  const renderLecturerView = () => {
    // Aggregate ratings by report_id
    const summary = ratings.reduce((acc, r) => {
      const existing = acc.find(s => s.report_id === r.report_id);
      if (existing) {
        existing.num_ratings += 1;
        existing.total_rating += r.rating;
      } else {
        acc.push({
          report_id: r.report_id,
          class_name: r.class_name,
          course_name: r.course_name,
          total_rating: r.rating,
          num_ratings: 1,
        });
      }
      return acc;
    }, []);
    summary.forEach(s => (s.average_rating = s.total_rating / s.num_ratings));

    return (
      <div className="row">
        <div className="col-12">
          <div className="card">
            <div className="card-header">
              <h5 className="card-title mb-0">Lecture Ratings Summary</h5>
            </div>
            <div className="card-body table-responsive">
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th>Class</th>
                    <th>Average Rating</th>
                    <th>Number of Ratings</th>
                  </tr>
                </thead>
                <tbody>
                  {summary.map((r) => (
                    <tr key={r.report_id}>
                      <td>
                        <strong>{r.class_name}</strong>
                        <br />
                        <small className="text-muted">{r.course_name}</small>
                      </td>
                      <td>{renderStarRating(r.average_rating)}</td>
                      <td>{r.num_ratings}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="container-fluid">
      <Header />
      <div className="row">
        <Sidebar />
        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">
            <h1 className="h2">{user?.role === 'student' ? 'My Ratings' : 'Teaching Ratings'}</h1>
            <div className="btn-toolbar mb-2 mb-md-0">
              <div className="btn-group me-2">
                <button type="button" className="btn btn-sm btn-outline-secondary" onClick={fetchRatingsData}>
                  <i className="fas fa-sync-alt me-1"></i> Refresh
                </button>
              </div>
            </div>
          </div>

          {error && <div className="alert alert-danger">{error}</div>}

          {user?.role === 'student' ? renderStudentView() : renderLecturerView()}

          <Modal show={showModal} onHide={() => setShowModal(false)}>
            <Modal.Header closeButton>
              <Modal.Title>Rate Lecture: {selectedReport?.course_name}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <Form>
                <Form.Group className="mb-3">
                  <Form.Label>Category</Form.Label>
                  <Form.Select value={categoryValue} onChange={(e) => setCategoryValue(e.target.value)}>
                    <option value="lecture">Lecture</option>
                    <option value="content">Content</option>
                    <option value="delivery">Delivery</option>
                    <option value="general">General</option>
                  </Form.Select>
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Rating (1–5)</Form.Label>
                  <Form.Control
                    type="number"
                    min={1}
                    max={5}
                    value={ratingValue}
                    onChange={(e) => setRatingValue(parseInt(e.target.value))}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Comment</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={3}
                    value={commentValue}
                    onChange={(e) => setCommentValue(e.target.value)}
                  />
                </Form.Group>
              </Form>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={() => setShowModal(false)}>Cancel</Button>
              <Button variant="primary" onClick={handleSubmitRating}>Submit Rating</Button>
            </Modal.Footer>
          </Modal>
        </main>
      </div>
    </div>
  );
};

export default Ratings;
