// Monitoring.jsx
import React, { useState, useEffect } from 'react';
import Header from '../common/Header';
import Sidebar from '../common/Sidebar';
import LoadingSpinner from '../common/LoadingSpinner';
import { useAuth } from '../../context/AuthContext';
import { monitoringService } from '../../services/monitoringService';

const Monitoring = () => {
  const { user } = useAuth();
  const [activities, setActivities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeRole, setActiveRole] = useState(user?.role || 'student');
  const [searchTerm, setSearchTerm] = useState('');
  const roles = ['lecturer', 'student', 'program_leader', 'principal_lecturer'];

  useEffect(() => {
    fetchActivities();
  }, [activeRole, searchTerm]);

  const fetchActivities = async () => {
    try {
      setLoading(true);
      const res = await monitoringService.getAll(); // fetch all monitoring logs
      let filtered = res.data || [];

      // Filter by role
      filtered = filtered.filter(a => a.role === activeRole);

      // Search filter
      if (searchTerm) {
        filtered = filtered.filter(a =>
          a.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
          a.action.toLowerCase().includes(searchTerm.toLowerCase())
        );
      }

      setActivities(filtered);
    } catch (err) {
      console.error('Error fetching activities:', err);
      setActivities([]);
    } finally {
      setLoading(false);
    }
  };

  const getActivityIcon = (action) => {
    switch (action) {
      case 'rating_added': return 'bi-star-fill text-warning';
      case 'report_viewed': return 'bi-eye text-primary';
      case 'report_submitted': return 'bi-file-earmark-text text-success';
      default: return 'bi-activity text-secondary';
    }
  };

  const getActivityBadge = (action) => {
    switch (action) {
      case 'rating_added': return 'bg-warning';
      case 'report_viewed': return 'bg-primary';
      case 'report_submitted': return 'bg-success';
      default: return 'bg-secondary';
    }
  };

  if (loading) return <LoadingSpinner message="Loading activities..." />;

  return (
    <div className="container-fluid">
      <Header />
      <div className="row">
        <Sidebar />
        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <div className="d-flex justify-content-between flex-wrap align-items-center pt-3 pb-2 mb-3 border-bottom">
            <h1 className="h2">Monitoring</h1>
            <input
              type="text"
              className="form-control w-25"
              placeholder="Search activities..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
            />
          </div>

          {/* Role Tabs */}
          <ul className="nav nav-tabs mb-3">
            {roles.map(role => (
              <li className="nav-item" key={role}>
                <button
                  className={`nav-link ${activeRole === role ? 'active' : ''}`}
                  onClick={() => setActiveRole(role)}
                >
                  {role.replace('_', ' ').toUpperCase()}
                </button>
              </li>
            ))}
          </ul>

          {/* Activity Table */}
          <div className="card">
            <div className="card-header">
              <h5 className="card-title mb-0">
                Activities - {activeRole.replace('_', ' ').toUpperCase()}
              </h5>
            </div>
            <div className="card-body table-responsive">
              {activities.length === 0 ? (
                <p className="text-center py-3">No activities found</p>
              ) : (
                <table className="table table-striped">
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>User</th>
                      <th>Action</th>
                      <th>Description</th>
                      <th>Module</th>
                      <th>Report ID</th>
                      <th>Date</th>
                    </tr>
                  </thead>
                  <tbody>
                    {activities.map(a => (
                      <tr key={a.id}>
                        <td>{a.id}</td>
                        <td>{a.user_name || a.user_id}</td>
                        <td>
                          <span className={`badge ${getActivityBadge(a.action)}`}>
                            {a.action.replace('_', ' ')}
                          </span>
                        </td>
                        <td>{a.description}</td>
                        <td>{a.module}</td>
                        <td>{a.report_id || '-'}</td>
                        <td>{new Date(a.created_at).toLocaleString()}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default Monitoring;
