// frontend/src/pages/Ratings.js
import React, { useState, useEffect } from "react";
import Header from "../components/common/Header";
import Sidebar from "../components/common/Sidebar";
import { useAuth } from "../context/AuthContext";
import api from "../services/api"; // axios instance with baseURL
import { Modal, Button, Form } from "react-bootstrap";

const Ratings = () => {
  const { user } = useAuth();
  const [ratings, setRatings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Modal state
  const [showModal, setShowModal] = useState(false);
  const [selectedReport, setSelectedReport] = useState(null);
  const [ratingValue, setRatingValue] = useState(5);
  const [categoryValue, setCategoryValue] = useState("lecture");
  const [commentValue, setCommentValue] = useState("");

  useEffect(() => {
    fetchRatings();
  }, []);

  const fetchRatings = async () => {
    try {
      setLoading(true);
      const res = await api.get("/ratings"); // token is added automatically by axios interceptor
      setRatings(res.data.ratings || []);
      setError("");
    } catch (err) {
      console.error(err);
      setError("Failed to fetch ratings");
    } finally {
      setLoading(false);
    }
  };

  const openModal = (report) => {
    setSelectedReport(report);
    setRatingValue(report.rating || 5);
    setCategoryValue(report.rating_type || "lecture");
    setCommentValue(report.comment || "");
    setShowModal(true);
  };

  const submitRating = async () => {
    try {
      await api.post("/ratings", {
        report_id: selectedReport.report_id,
        rating: ratingValue,
        rating_type: categoryValue,
        comment: commentValue,
      });
      setShowModal(false);
      fetchRatings();
      alert("Rating submitted successfully!");
    } catch (err) {
      console.error(err);
      alert("Failed to submit rating");
    }
  };

  const renderStarRating = (rating) => (
    <div>
      <span style={{ color: "#ffc107", fontSize: "1.1rem" }}>
        {"★".repeat(Math.floor(rating))}{"☆".repeat(5 - Math.floor(rating))}
      </span>
      <span className="ms-2">({rating}/5)</span>
    </div>
  );

  const renderStudentView = () => (
    <div className="table-responsive">
      {ratings.length === 0 ? (
        <p>No ratings yet. Go to Reports to rate lectures.</p>
      ) : (
        <table className="table table-hover">
          <thead>
            <tr>
              <th>Class</th>
              <th>Rating</th>
              <th>Category</th>
              <th>Comment</th>
              <th>Date</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {ratings.map((r) => (
              <tr key={r.rating_id || r.report_id}>
                <td>
                  <strong>{r.class_name}</strong>
                  <br />
                  <small>{r.course_name}</small>
                </td>
                <td>{r.rating ? renderStarRating(r.rating) : "Not rated"}</td>
                <td>{r.rating_type || "-"}</td>
                <td>{r.comment || "-"}</td>
                <td>{new Date(r.date_of_lecture).toLocaleDateString()}</td>
                <td>
                  <Button size="sm" onClick={() => openModal(r)}>
                    Rate / Edit
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );

  const renderLecturerView = () => (
    <div className="table-responsive">
      <table className="table table-hover">
        <thead>
          <tr>
            <th>Class</th>
            <th>Average Rating</th>
            <th>Number of Ratings</th>
          </tr>
        </thead>
        <tbody>
          {ratings.map((r) => (
            <tr key={r.report_id}>
              <td>
                <strong>{r.class_name}</strong>
                <br />
                <small>{r.course_name}</small>
              </td>
              <td>{r.rating ? renderStarRating(r.rating) : "Not rated"}</td>
              <td>{r.rating_id ? 1 : 0}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  if (loading) return <p>Loading ratings...</p>;

  return (
    <div className="container-fluid">
      <Header />
      <div className="row">
        <Sidebar />
        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <h2>{user.role === "student" ? "My Ratings" : "Teaching Ratings"}</h2>
          {error && <div className="alert alert-danger">{error}</div>}
          {user.role === "student" ? renderStudentView() : renderLecturerView()}

          <Modal show={showModal} onHide={() => setShowModal(false)}>
            <Modal.Header closeButton>
              <Modal.Title>Rate Lecture: {selectedReport?.course_name}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <Form>
                <Form.Group className="mb-3">
                  <Form.Label>Category</Form.Label>
                  <Form.Select
                    value={categoryValue}
                    onChange={(e) => setCategoryValue(e.target.value)}
                  >
                    <option value="lecture">Lecture</option>
                    <option value="content">Content</option>
                    <option value="delivery">Delivery</option>
                    <option value="general">General</option>
                  </Form.Select>
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Rating (1–5)</Form.Label>
                  <Form.Control
                    type="number"
                    min={1}
                    max={5}
                    value={ratingValue}
                    onChange={(e) => setRatingValue(parseInt(e.target.value))}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Comment</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={3}
                    value={commentValue}
                    onChange={(e) => setCommentValue(e.target.value)}
                  />
                </Form.Group>
              </Form>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={() => setShowModal(false)}>
                Cancel
              </Button>
              <Button variant="primary" onClick={submitRating}>
                Submit Rating
              </Button>
            </Modal.Footer>
          </Modal>
        </main>
      </div>
    </div>
  );
};

export default Ratings;
