// pages/Ratings.jsx
import React, { useState, useEffect } from 'react';
import Header from '../components/common/Header';
import Sidebar from '../components/common/Sidebar';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import { Modal, Button, Form } from 'react-bootstrap';

const Ratings = () => {
  const { user } = useAuth();
  const [ratings, setRatings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Modal state
  const [showModal, setShowModal] = useState(false);
  const [selectedReport, setSelectedReport] = useState(null);
  const [ratingValue, setRatingValue] = useState(5);
  const [categoryValue, setCategoryValue] = useState('lecture');
  const [commentValue, setCommentValue] = useState('');

  useEffect(() => {
    fetchRatingsData();
  }, []);

  const fetchRatingsData = async () => {
    try {
      setLoading(true);

      let endpoint = '/ratings';
      if (user?.role === 'lecturer' || user?.role === 'program_leader') {
        // Get all ratings for reports lecturer teaches
        endpoint = '/ratings';
      } else if (user?.role === 'student') {
        // Get only their ratings
        endpoint = '/ratings';
      }

      const response = await api.get(endpoint);
      setRatings(response.data.ratings || []);
    } catch (err) {
      console.error('Error fetching ratings:', err);
      setError('Failed to load ratings data');
    } finally {
      setLoading(false);
    }
  };

  const openRatingModal = (report) => {
    setSelectedReport(report);
    setRatingValue(report.rating || 5);
    setCategoryValue(report.rating_type || 'lecture');
    setCommentValue(report.comment || '');
    setShowModal(true);
  };

  const handleSubmitRating = async () => {
    try {
      await api.post('/ratings', {
        report_id: selectedReport.report_id,
        rating: ratingValue,
        rating_type: categoryValue,
        comment: commentValue,
      });
      setShowModal(false);
      fetchRatingsData();
      alert('Rating submitted successfully!');
    } catch (err) {
      console.error('Error submitting rating:', err);
      alert('Failed to submit rating');
    }
  };

  const renderStarRating = (rating) => (
    <div className="d-flex align-items-center">
      <span className="text-warning me-2">
        {'★'.repeat(Math.floor(rating))}{'☆'.repeat(5 - Math.floor(rating))}
      </span>
      <span className="badge bg-primary">{rating}/5</span>
    </div>
  );

  if (loading) return <div>Loading ratings...</div>;

  return (
    <div className="container-fluid">
      <Header />
      <div className="row">
        <Sidebar />
        <main className="col-md-9 ms-sm-auto col-lg-10 px-md-4">
          <div className="d-flex justify-content-between align-items-center pt-3 pb-2 mb-3 border-bottom">
            <h1 className="h2">Lecture Ratings</h1>
            <button className="btn btn-sm btn-outline-secondary" onClick={fetchRatingsData}>
              Refresh
            </button>
          </div>

          {error && <div className="alert alert-danger">{error}</div>}

          <div className="table-responsive">
            <table className="table table-hover table-bordered">
              <thead>
                <tr>
                  <th>Class</th>
                  <th>Week</th>
                  <th>Date</th>
                  <th>Course Name</th>
                  <th>Course Code</th>
                  <th>Actual / Registered</th>
                  <th>Venue</th>
                  <th>Time</th>
                  <th>Topic</th>
                  <th>Learning Outcomes</th>
                  <th>Recommendations</th>
                  <th>Rating</th>
                  <th>Category</th>
                  <th>Comment</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {ratings.length === 0 ? (
                  <tr>
                    <td colSpan="15" className="text-center">No ratings yet.</td>
                  </tr>
                ) : (
                  ratings.map((r) => (
                    <tr key={r.rating_id || r.report_id}>
                      <td>{r.class_name}</td>
                      <td>{r.week_of_reporting}</td>
                      <td>{new Date(r.date_of_lecture).toLocaleDateString()}</td>
                      <td>{r.course_name}</td>
                      <td>{r.course_code}</td>
                      <td>{r.actual_students_present} / {r.total_registered_students}</td>
                      <td>{r.venue}</td>
                      <td>{r.scheduled_lecture_time}</td>
                      <td>{r.topic_taught}</td>
                      <td>{r.learning_outcomes}</td>
                      <td>{r.lecturer_recommendations || 'N/A'}</td>
                      <td>{r.rating ? renderStarRating(r.rating) : '-'}</td>
                      <td>{r.rating_type || '-'}</td>
                      <td>{r.comment || '-'}</td>
                      <td>
                        <button className="btn btn-sm btn-outline-primary" onClick={() => openRatingModal(r)}>
                          Rate / Edit
                        </button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>

          {/* Rating Modal */}
          <Modal show={showModal} onHide={() => setShowModal(false)}>
            <Modal.Header closeButton>
              <Modal.Title>Rate Lecture: {selectedReport?.course_name}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <Form>
                <Form.Group className="mb-3">
                  <Form.Label>Category</Form.Label>
                  <Form.Select value={categoryValue} onChange={(e) => setCategoryValue(e.target.value)}>
                    <option value="lecture">Lecture</option>
                    <option value="content">Content</option>
                    <option value="delivery">Delivery</option>
                    <option value="general">General</option>
                  </Form.Select>
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Rating (1–5)</Form.Label>
                  <Form.Control type="number" min={1} max={5} value={ratingValue} onChange={(e) => setRatingValue(parseInt(e.target.value))}/>
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Comment</Form.Label>
                  <Form.Control as="textarea" rows={3} value={commentValue} onChange={(e) => setCommentValue(e.target.value)}/>
                </Form.Group>
              </Form>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={() => setShowModal(false)}>Cancel</Button>
              <Button variant="primary" onClick={handleSubmitRating}>Submit Rating</Button>
            </Modal.Footer>
          </Modal>

        </main>
      </div>
    </div>
  );
};

export default Ratings;
